#include  <bits/stdc++.h>using namespace std;#define int long longvector <int> vi;int n,m,k,res;int pin(int a, int b){    return ((b>m)? a+b-m:a);}main(){    ios_base::sync_with_stdio(false);cin.tie(0);cout.tie(0);    cin >> n >> m >> k;    for (int i=1;i<=n;i++)    {        int ai,bi; cin >> ai >> bi;        vi.push_back(pin(ai,bi));    }    sort(vi.begin(),vi.end());    for (int c : vi)    if (k>=c) res++,k-=c;    else break;    cout << res;}